import './App.css';
import React, { useState } from 'react';
import PlayerStats from './PlayerStats';

/*function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>League of Legends Player Stats</h1>
        <PlayerStats />
      </header>
    </div>
  );
}*/

function App() {
  const [summonerName, setSummonerName] = useState('');
  const [tagline, setTagline] = useState('');
  const [region, setRegion] = useState('');
  const [championName, setChampionName] = useState('');

  const handleSummonerNameChange = (event) => {
    setSummonerName(event.target.value);
  };

  const handleTaglineChange = (event) => {
    setTagline(event.target.value);
  };

  const handleRegionChange = (event) => {
    setRegion(event.target.value);
  };

  const handleChampionNameChange = (event) => {
    setChampionName(event.target.value);
  };

  const handleSubmit = () => {
    // Handle submission of summonerName, tagline, region, and championName
    console.log(`Summoner Name: ${summonerName}, Tagline: ${tagline}, Region: ${region}, Champion Name: ${championName}`);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>League of Legends Player Stats</h1>
        <div className="input-container">
          <label>Summoner Name:</label>
          <input
            type="text"
            value={summonerName}
            onChange={handleSummonerNameChange}
            placeholder="Enter Name"
            className="main-input"
          />
        </div>
        <div className="input-container">
          <label>Tagline:</label>
          <input
            type="text"
            value={tagline}
            onChange={handleTaglineChange}
            placeholder="Enter Tagline"
            className="main-input" 
          />
        </div>
        <button onClick={handleSubmit}>Submit</button>
      </header>
    </div>
  );
}

export default App;
