import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './PlayerStats.css';
import { Bar } from 'react-chartjs-2';

const PlayerStats = () => {
  const [playerStats, setPlayerStats] = useState([]);
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    const fetchPlayerStats = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/player-stats');
        console.log('Response:', response.data);  // Log the response data
        if (Array.isArray(response.data)) {
          setPlayerStats(response.data);
          generateChartData(response.data);
        } else {
          console.error('Expected an array but got:', response.data);
        }
      } catch (error) {
        console.error('Error fetching player stats:', error);
      }
    };

    fetchPlayerStats();
  }, []);

  const generateChartData = (data) => {
    const chartLabels = [];
    const winPercentages = [];
    const durations = [];

    data.forEach((item) => {
      chartLabels.push(item.match_id.toString()); // Use match_id as labels
      winPercentages.push(calculateWinPercentage(item));
      durations.push(item.Duration);
    });

    setChartData({
      labels: chartLabels,
      datasets: [
        {
          label: 'Win Percentage',
          data: winPercentages,
          backgroundColor: 'rgba(75,192,192,0.6)',
          borderColor: 'rgba(75,192,192,1)',
          borderWidth: 1,
          yAxisID: 'y-axis-1',
        },
        {
          label: 'Duration',
          data: durations,
          type: 'line',
          fill: false,
          borderColor: 'rgba(255,99,132,1)',
          borderWidth: 2,
          yAxisID: 'y-axis-2',
        },
      ],
    });
  };

  const calculateWinPercentage = (item) => {
    const totalGames = playerStats.length;
    const winCount = playerStats.filter((stat) => stat.Win).length;
    return (winCount / totalGames) * 100;
  };

  return (
    <div>
      <div>
        {chartData && chartData.labels && chartData.labels.length > 0 ? (
          <Bar
            data={chartData}
            options={{
              responsive: true,
              plugins: {
                legend: {
                  position: 'top',
                },
              },
              scales: {
                y: {
                  type: 'linear',
                  display: true,
                  position: 'left',
                  id: 'y-axis-1',
                  title: {
                    display: true,
                    text: 'Win Percentage',
                  },
                  grid: {
                    drawOnChartArea: false,
                  },
                },
                y2: {
                  type: 'linear',
                  display: true,
                  position: 'right',
                  id: 'y-axis-2',
                  title: {
                    display: true,
                    text: 'Duration',
                  },
                },
              },
            }}
          />
        ) : (
          <p>Loading chart...</p>
        )}
      </div>
    </div>
  );
};

export default PlayerStats;
