function App() {
  const [summonerName, setSummonerName] = useState('');
  const [tagline, setTagline] = useState('');

  const handleSummonerNameChange = (event) => {
    setSummonerName(event.target.value);
  };

  const handleTaglineChange = (event) => {
    setTagline(event.target.value);
  };

  const handleSubmit = () => {
    // Handle submission of summonerName and tagline, e.g., send to backend
    console.log(`Summoner Name: ${summonerName}, Tagline: ${tagline}`);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>League of Legends Player Stats</h1>
        <div className="input-container">
          <label>Summoner Name:</label>
          <input
            type="text"
            value={summonerName}
            onChange={handleSummonerNameChange}
            placeholder="Enter Name"
            className="main-input" // Apply the CSS class here
          />
        </div>
        <div className="input-container">
          <label>Tagline:</label>
          <input
            type="text"
            value={tagline}
            onChange={handleTaglineChange}
            placeholder="Enter Tagline"
            className="main-input" // Apply the CSS class here
          />
        </div>
        <button onClick={handleSubmit}>Submit</button>
      </header>
    </div>
  );
}

export default App;
